a_st = 'acgdggaag'

@calculate_time
def brute_force(A):
    count = 0
    for i in range(len(A)):
        if A[i] == 'a':
            for j in range(i+1, len(A)):
                if A[j] == 'g':
                    count += 1
    return count

@calculate_time
def solve(A):
    count = 0
    ans = 0
    for i in A:
        if i == 'a':
            count += 1
        elif i == 'g':
            ans += count
    return ans

@calculate_time
def solve_cap(A):
    count = 0
    ans = 0
    for i in A:
        if i == 'A':
            count += 1
        elif i == 'G':
            ans += count
    return ans

